import React, { Component } from "react";


import { ReactiveMap } from "@appbaseio/reactivemaps";
import "./App.css";

class App extends Component {
  onData(resource) {
    const stars = [];
    const { rating, currency, address, cuisine } = resource;
    for (let x = 0; x < rating; x++) {
      stars.push(
        <span key={x}>
          <i className="fa fa-star" />
        </span>
      );
    }

    const result = {
      title: resource.name,
      description: (
        <div>
          <p>{address}</p>
          <span className="tag">{currency}</span>
          <span className="tag">{cuisine}</span>
          <div>Avg. Customer Reviews : {stars}</div>
        </div>
      )
    };
    return result;
  }

  onPopoverClick(marker) {
    return (
      <div
        className="row"
        style={{ margin: "0", maxWidth: "300px", paddingTop: 10 }}
      >
        <div className="col s12">
          <div>
            <strong>{marker.name}</strong>
          </div>
          <p style={{ margin: "5px 0", lineHeight: "18px" }}>
            {marker.address}
          </p>
        </div>
      </div>
    );
  }

  render() {
    return (
      <div className="container-fluid">
        <script async src="https://cse.google.com/cse.js?cx=009696093307156488668:8us5tiyr6zs"></script>
        <div class="gcse-search"></div>
      </div>
    );
  }
}

export default App;
